show databases;
create database college;
use college;
create table customers;
select * from customers limit 20;
-- 1. What is the total revenue generated by male vs. female customers?
SELECT gender,sum(purchase_amount) as revenue
FROM customers
group by gender;

-- 2. Which customers used a discount but still spent more than the average purchase amount?
select customer_id,purchase_amount
from customers
where discount_applied = 'Yes' and purchase_amount >=(select avg(purchase_amount) from customers);

-- 3. Which are the top 5 products with the highest average review rating?
select item_purchased,avg(review_rating) as 'Average product rating'
from customers
group by item_purchased
order by avg(review_rating) desc;

-- 4. Compare the average Purchase Amounts between Standard and Express Shipping.
select shipping_type,
round(avg(purchase_amount),2)
from customers
where shipping_type in('Standard','Express')
group by shipping_type;
-- 5. Do subscribed customers spend more? Compare average spend and total revenue between subscribed and non-subscribed customers.
select subscription_status,
count(customer_id) as total_customers,
round(avg(purchase_amount),2) as avg_spend,
round(sum(purchase_amount),2) as total_revenue
from customers
group by subscription_status
order by total_revenue,avg_spend;

-- 6. Which 5 product has the highest percentage of purchases with discounts applied?
select item_purchased,
round(100*sum(case when discount_applied ='Yes' then 1 else 0 end)/count(*) * 100,2)as discount_rate
from customers
group by item_purchased
order by discount_rate desc
limit 5;
-- 7. Segment customers into New, Returning, and Loyal based on their total number of previous purchases, and compare their total revenue.
with customer_type as(
select customer_id,previous_purchases,
case 
 when previous_purchases = 1 then 'New'
 when previous_purchases between 2 and 10 then 'Returning'
 else 'Loyal'
 end as customer_segment
from customers)
select customer_segment, count(*) as 'Number_of_customers'
from customer_type
group by customer_segment; 
-- 8. What are the 3 most purchased products within each category?
with item_counts as(
select category,
item_purchased,
count(customer_id) as total_orders,
row_number() over(partition by category order by count(customer_id)desc) as item_rank
from customers
group by category, item_purchased
)
select item_rank,category,item_purchased,total_orders
from item_counts
where item_rank<=3;
-- Q9. Are customers who are repeat buyers (more than 5 previous purchases) also likely to subscribe?

SELECT subscription_status,
       COUNT(customer_id) AS repeat_buyers
FROM customers
WHERE previous_purchases > 5
GROUP BY subscription_status;

-- Q10. What is the revenue contribution of each age group?
SELECT age_group,
       SUM(purchase_amount) AS total_revenue
FROM customers
GROUP BY age_group
ORDER BY total_revenue DESC;


